mapper.select:
SELECT R.r_id, R.education, R_R.region, R.job, R.payment_type, R.payment, R.ob_type, R.disease, R.career, R.pr, R.m_num, R.title , (CASE WHEN R.r_id IN ( SELECT M.r_default FROM MEMBER M WHERE M.m_num = R.m_num ) THEN 'True' ELSE 'False' END) AS r_default FROM RESUME R INNER JOIN RESUME_REGION R_R ON R.r_id = R_R.r_id
AND R.m_num = #{memberId}
GROUP BY R.r_id

SELECT R.r_id, R.education, R_R.region, R.job, R.payment_type, R.payment, R.ob_type, R.disease, R.career, R.pr, R.m_num, R.title, (SELECT M.name FROM MEMBER M WHERE M.m_num = R.m_num) AS name FROM RESUME R INNER JOIN RESUME_REGION R_R ON R.r_id = R_R.r_id
and R.payment_type in
#{ptype}
and R.disease in
#{d}
and R.ob_type in
#{ob}
and R_R.region in
#{p}
and R.education in
#{e}
and R.job LIKE CONCAT('%', #{keyword}, '%')
GROUP BY R.r_id
ORDER BY R.r_id DESC
ORDER BY R.payment ASC
