mapper.update:
UPDATE member
SET email = #{email}, phone = #{phone},
profil = #{profil}
WHERE id = #{id}

UPDATE member
SET password = #{password}, email =
#{email}, phone = #{phone},
profil = #{profil}
WHERE m_num = #{m_num}

mapper.select:
SELECT password
FROM member
WHERE m_num = #{m_num}

